import java.util.Scanner;class bankaccount {    public static void main(String[] args) {        int currentMoney = 100;        while(currentMoney>0){            Scanner input = new Scanner(System.in);            System.out.println("Enter the amount");            int amount = input.nextInt();            System.out.println("Enter 1 for deposit, 2 for withdrawal");            int operation = input.nextInt();            if (operation == 1) {                int deposit = amount + currentMoney;                System.out.println("Current money: " + deposit);                currentMoney += amount;            }            else if (operation ==2){                int withdrawal = currentMoney - amount;                System.out.println("Current money:" + withdrawal);                currentMoney -= amount;                if ( currentMoney == 0 ){                    System.out.println("You have no money left");            }            }        }    }}